pragma solidity ^0.8.13;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {NftTokenInfo} from "./lib/NftTokenInfo.sol";
import {SmartAccountBase, NotAuthorized, InvalidInput} from "./lib/SmartAccountBase.sol";

/**
 * @title A smart contract account owned by a single ERC721 token
 */
contract SmartAccount is SmartAccountBase {
  using NftTokenInfo for address;

  uint256 public state;

  constructor() SmartAccountBase() {}


  /// @dev allows eth transfers by default
  receive() external payable virtual override {}



  function getPrincipal(address assetToken) external view virtual override returns (uint256 total) {
    total = IERC20(assetToken).balanceOf(address(this));
  }

  function getInterest(address /* assetToken */) external view virtual override returns (uint256 total) {
    return 0;
  }

  function getRewards(address /* assetToken */) external view virtual override returns (uint256 total) {
    return 0;
  }

  function getCovalentBonds(address nftContractAddress, uint256 nftTokenId) external view virtual override returns (uint256 total) {
    if (nftContractAddress.isERC1155()) {
      total = IERC1155(nftContractAddress).balanceOf(address(this), nftTokenId);
    } else {
      total = IERC721(nftContractAddress).balanceOf(address(this));
    }
  }



  /// @dev executes a low-level call against an account if the caller is authorized to make calls
  function execute(
    address to,
    uint256 value,
    bytes calldata data,
    uint8 operation
  )
    public
    payable
    virtual
    override
    onlyValidSigner
    returns (bytes memory)
  {
    require(operation == 0, "Only call operations are supported");
    ++state;

    // Perform custom checks/updates from within a custom controller
    _onExecute(to, value, data, operation);

    // Execute Call on Account
    return _call(to, value, data);
  }


  function handleTokenUpdate(
    bool isReceiving,
    address assetToken,
    uint256 assetAmount
  )
    public
    virtual
    override
    onlyValidSigner
  {
    // Perform custom checks/updates from within a custom controller
    _onUpdateToken(isReceiving, assetToken, assetAmount);
  }

  function handleNFTUpdate(
    bool isReceiving,
    address tokenContract,
    uint256 tokenId,
    uint256 tokenAmount
  )
    public
    virtual
    override
    onlyValidSigner
  {
    // Perform custom checks/updates from within a custom controller
    _onUpdateNFT(isReceiving, tokenContract, tokenId, tokenAmount);
  }

  function handleNFTBatchUpdate(
    bool isReceiving,
    address tokenContract,
    uint256[] calldata tokenIds,
    uint256[] calldata tokenAmounts
  )
    public
    virtual
    override
    onlyValidSigner
  {
    // Perform custom checks/updates from within a custom controller
    _onUpdateNFTBatch(isReceiving, tokenContract, tokenIds, tokenAmounts);
  }
}

contracts/tokens/BufficornZK.sol.content:
// SPDX-License-Identifier: MIT
// Written by: Rob Secord (https://twitter.com/robsecord)
// Co-founder @ Charged Particles - Visit: https://charged.fi
// Co-founder @ Taggr             - Visit: https://taggr.io

pragma solidity ^0.8.13;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {ERC721, ERC721Enumerable} from "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {ISmartAccountController} from "../interfaces/ISmartAccountController.sol";
import {IDynamicTraits} from "../interfaces/IDynamicTraits.sol";

/**
 * @dev todo...
 */
contract BufficornZK is ISmartAccountController, IDynamicTraits, Ownable, ERC721Enumerable {
  using Strings for uint256;

  // Base portion of the Token Metadata URI (format: base-uri/tokenId/traitsMap)
  string internal _tokenUriBase;

  // TokenId => Traits BitMap
  mapping (uint256 => uint256) internal _traitBits;

  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Initialization

  constructor(string memory name, string memory symbol)
    ERC721(name, symbol) Ownable() {}


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Minting Logic

  // For minting container-NFTs that have no initial traits
  function mint(uint256 tokenId) external virtual {
    _mint(tokenId, 0);
  }

  // For minting child-NFTs that have initial fixed traits
  function mintWithTraits(uint256 tokenId, uint256 traits) external virtual {
    _mint(tokenId, traits);
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Customizable Traits Logic

  function getTraits(uint256 tokenId) external view override returns (uint256) {
    return _traitBits[tokenId];
  }

  function hasTrait(uint256 tokenId, uint256 trait) external view override returns (bool) {
    uint256 bit = _traitBits[tokenId] & (1 << trait);
    return bit > 0;
  }

  function traitCount(uint256 tokenId) external view override returns (uint256 totalTraits) {
    uint256 map = _traitBits[tokenId];
    // Brian Kerninghan bit-counting method = O(log(n))
    while (map != 0) {
      map &= (map - 1);
      totalTraits += 1;
    }
  }

  function _addTraits(uint256 tokenId, uint256 traits) internal returns (uint256) {
    _traitBits[tokenId] |= traits;
    return _traitBits[tokenId];
  }

  function _removeTraits(uint256 tokenId, uint256 traits) internal returns (uint256) {
    uint256 mask = traits ^ (2 ** 256 - 1); // negate to find the traits to keep
    _traitBits[tokenId] &= mask;
    return _traitBits[tokenId];
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Standard NFT Logic

  function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
    _requireMinted(tokenId);
    string memory baseURI = _baseURI();
    return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, tokenId.toString(), "/", _traitBits[tokenId].toString())) : "";
  }

  function setBaseURI(string memory newBase) external onlyOwner {
    _tokenUriBase = newBase;
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return _tokenUriBase;
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // SmartAccount Controller Logic

  function onExecute(
    address,
    uint256,
    bytes calldata,
    uint8
  )
    external
    virtual
    override
    returns (string memory revertReason)
  {
    return ""; // success
  }

  function onUpdateToken(
    bool isReceiving,
    uint256, // this chain
    address, // this contract
    uint256 tokenId,
    address receivedAssetToken,
    uint256 receivedAssetAmount
  )
    external
    virtual
    override
  {
    // no-op
  }

  function onUpdateNFT(
    bool isReceiving,
    uint256, // this chain
    address, // this contract
    uint256 tokenId,
    address receivedTokenContract,
    uint256 receivedTokenId,
    uint256
  )
    external
    virtual
    override
  {
    if (IERC165(receivedTokenContract).supportsInterface(type(IDynamicTraits).interfaceId)) {
      uint256 newTraits = IDynamicTraits(receivedTokenContract).getTraits(receivedTokenId);
      if (isReceiving) {
        _addTraits(tokenId, newTraits);
      } else {
        _removeTraits(tokenId, newTraits);
      }
    }
  }

  function onUpdateNFTBatch(
    bool isReceiving,
    uint256, // this chain
    address, // this contract
    uint256 tokenId,
    address receivedTokenContract,
    uint256[] calldata receivedTokenIds,
    uint256[] calldata
  )
    external
    virtual
    override
  {
    uint256 i;
    uint256 t;
    uint256 n = receivedTokenIds.length;
    if (IERC165(receivedTokenContract).supportsInterface(type(IDynamicTraits).interfaceId)) {
      for (; i < n; i++) {
        t = IDynamicTraits(receivedTokenContract).getTraits(receivedTokenIds[i]);
        if (isReceiving) {
          _addTraits(tokenId, t);
        } else {
          _removeTraits(tokenId, t);
        }
      }
    }
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Custom Interfaces

  /// @dev Returns true if a given interfaceId is supported by this account. This method can be
  /// extended by an override.
  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(IERC165, ERC721Enumerable)
    returns (bool)
  {
    return
      interfaceId == type(ISmartAccountController).interfaceId ||
      interfaceId == type(IDynamicTraits).interfaceId ||
      super.supportsInterface(interfaceId);
  }


  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
  // Internal Functions

  function _mint(uint256 tokenId, uint256 traits) internal {
    // Note: Do NOT set traits when bridging a Bufficorn-NFT.
    //       The "traits" param is ONLY used for minting Trait-NFTs
    //       which can be minted from this contract, or another contract.
    //       When bridging a Bufficorn, this contract will retain the last-known
    //       state for a Bufficorns nested traits on zkSync.
    if (traits > 0) {
      _traitBits[tokenId] = traits;
    }
    _safeMint(_msgSender(), tokenId);
  }
}

contracts/tokens/ERC721All.sol.content:
// SPDX-License-Identifier: MIT
// Written by: Rob Secord (https://twitter.com/robsecord)
// Co-founder @ Charged Particles - Visit: https://charged.fi
// Co-founder @ Taggr             - Visit: https://taggr.io

pragma solidity ^0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";

import "../lib/ERC721.sol";

/**
 * @dev todo...
 */
contract ERC721All is Ownable, ERC721 {
  mapping(uint256 => bool) internal _activeTokens;

  constructor(string memory name, string memory symbol) ERC721(name, symbol) Ownable() {}

  /**
   * @dev Overrides {IERC721-balanceOf}.
   */
  function balanceOf(address owner) public view override returns (uint256) {
    require(owner != address(0), "ERC721: address zero is not a valid owner");
    if (_balances[owner] == 0 && _hasOwnToken(owner)) {
      return 1;
    }
    return _balances[owner];
  }

  /**
   * @dev Overrides {IERC721-ownerOf}.
   */
  function ownerOf(uint256 tokenId) public view override returns (address) {
    require(_isTokenActive(tokenId), "ERC721: invalid token ID");

    // If token has been transfered then _owners will be populated,
    // otherwise the token ID represents the initial owner
    address owner = _owners[tokenId];
    if (owner == address(0)) {
      owner = address(uint160(tokenId));
    }
    return owner;
  }

  function mint() public {
    _mint(_msgSender());
  }

  function _mint(address receiver) internal {
    // Token ID == Minter Address
    uint256 tokenId = uint256(uint160(receiver));

    require(receiver != address(0), "ERC721: mint to the zero address");
    require(!_isTokenActive(tokenId), "ERC721: token already minted");

    // Mark Token as Active
    _activeTokens[tokenId] = true;

    // Fire Transfer Event
    emit Transfer(address(0), receiver, tokenId);
  }

  function _transfer(
    address from,
    address to,
    uint256 tokenId
  ) internal override {
    require(ownerOf(tokenId) == from, "ERC721: transfer from incorrect owner");
    require(to != address(0), "ERC721: transfer to the zero address");

    _beforeTokenTransfer(from, to, tokenId);

    // Clear approvals from the previous owner
    _approve(address(0), tokenId);

    // Only if token has previously been transfered after mint
    if (_owners[tokenId] != address(0)) {
      _balances[from] -= 1;
    }
    _balances[to] += 1;
    _owners[tokenId] = to;

    emit Transfer(from, to, tokenId);

    _afterTokenTransfer(from, to, tokenId);
  }

  function _hasOwnToken(address owner) internal view returns (bool) {
    uint256 ownerTokenId = uint256(uint160(owner));
    address currentOwner = _owners[ownerTokenId];
    return (_isTokenActive(ownerTokenId) && (currentOwner == owner || currentOwner == address(0)));
  }

  function _isTokenActive(uint256 tokenId) internal view returns (bool) {
    // Check if Token is Active and Not Burned
    return (_activeTokens[tokenId] && _owners[tokenId] != _NULL_ADDRESS);
  }
}

contracts/tokens/ERC721i.sol.content:
// SPDX-License-Identifier: MIT
// Written by: Rob Secord (https://twitter.com/robsecord)
// Co-founder @ Charged Particles - Visit: https://charged.fi
// Co-founder @ Taggr             - Visit: https://taggr.io

pragma solidity ^0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../lib/ERC721iEnumerable.sol";

/**
 * @dev This implements a Pre-Mint version of {ERC721} that adds the ability to Pre-Mint
 * all the token ids in the contract as assign an initial owner for each token id.
 *
 * On-chain state for Pre-Mint does not need to be initially stored if Max-Supply is known.
 * Minting is a simple matter of assigning a balance to the pre-mint receiver,
 * and modifying the "read" methods to account for the pre-mint receiver as owner.
 * We use the Consecutive Transfer Method as defined in EIP-2309 to signal inital ownership.
 * Almost everything else remains standard.
 * We also default to the contract "owner" as the pre-mint receiver, but this can be changed.
 */
contract ERC721i is
  Ownable,
  ERC721iEnumerable
{
  /// @dev EIP-2309: https://eips.ethereum.org/EIPS/eip-2309
  event ConsecutiveTransfer(uint256 indexed fromTokenId, uint256 toTokenId, address indexed fromAddress, address indexed toAddress);

  /// @dev ERC721 Base Token URI
  string internal _baseTokenURI;

  /**
    * @dev Initializes the contract by setting a `name` and a `symbol` to the token collection,
    * as well as a `minter` and a `maxSupply` for pre-minting the collection.
    */
  constructor(
    string memory name,
    string memory symbol,
    string memory baseUri,
    address minter,
    uint256 maxSupply
  )
    ERC721(name, symbol)
    Ownable()
  {
    _baseTokenURI = baseUri;
    // Set vars defined in ERC721iEnumerable.sol
    _maxSupply = maxSupply;
    _preMintReceiver = minter;
  }

  /**
    * @dev Pre-mint the max-supply of token IDs to the minter account.
    * Token IDs are in base-1 sequential order.
    */
  function _preMint() internal {
    // Update balance for initial owner, defined in ERC721.sol
    _balances[_preMintReceiver] = _maxSupply;

    // Emit the Consecutive Transfer Event
    emit ConsecutiveTransfer(1, _maxSupply, address(0), _preMintReceiver);
  }

  function preMint() external onlyOwner {
    _preMint();
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return _baseTokenURI;
  }
}

