  function breakCovalentBond(
    address receiver,
    address contractAddress,
    uint256 tokenId,
    address nftTokenAddress,
    uint256 nftTokenId,
    uint256 nftTokenAmount
  ) external returns (bool success);
}","keccak256":"0x5f44bb288ef421632a74e7ac0929e2fcd16db6a0281a29865ee50805e26f91c9","license":"MIT"},"contracts/interfaces/IDynamicTraits.sol":{"content":"// SPDX-License-Identifier: MIT

